orderBottomForm.addEventListener("submit", function (e) {
  e.preventDefault();
  var data = new FormData(this);
  var messageText = 'Посетитель сайта задал вопрос\n\n' + 'Имя посетителя ' + data.get("person-name") + '\nТелефон: ' + data.get("person-phone") + '\nПредпочитаемый мессендже: ' + (!!data.get("person-messenger") ? data.get("person-messenger") : 'мессенджер не указан') + '\nEmail: ' + data.get("person-email") + '\nСообщение клиента: ' + data.get("person-message");
  console.log(messageText);
  sendMsg(messageText);
});

function sendMsg(data) {
  var url = 'https://api.telegram.org/bot' + TOKEN + '/sendMessage'; // токен бота
  var body = JSON.stringify({
    chat_id: CHAT_ID,
    parse_mode: 'Markdown',
    text: data
  });
  var xhr = new XMLHttpRequest();
  xhr.open('POST', url, true);

  // уведомление-модалка
  xhr.onload = function (oEvent) {
    if (xhr.status === 200) {
      document.body.classList.add('modal-view');
      document.body.classList.add('success-send');
    }
    if (xhr.status !== 200) {
      document.body.classList.add('modal-view');
      document.body.classList.add('error-send');
    }
  };
  xhr.setRequestHeader('Content-type', 'application/json; charset=utf-8');
  xhr.send(body);

  document.body.removeAttribute("class");
  document.body.classList.remove('call-form');
  document.body.classList.remove('order-online');
}
